            var messages = new List<Message>();

            for (var i = 0; i < count; i++)
            {
                messages.Add(new Message()
                {
                    Id = Guid.NewGuid(),
                    From = GetRandomalEmailAddress(),
                    To = GetRandomalEmailAddress(),
                    Subject = GetRandomalSubject(),
                    TimeStamp = DateTime.Now
                });
            }

            return messages;
        }

     

